import type {IconProps} from './Icon';

import {Icon} from './Icon';
export const socials = [
  'facebook',
  'instagram',
  'twitter',
  'tiktok',
  'youtube',
  'pinterest',
  'linkedin',
  'snapchat',
  'vimeo',
  'tumblr',
] as const;

export type Socials = (typeof socials)[number];
export function IconSocial({
  media,
  ...props
}: IconProps & {
  media: Socials;
}) {
  return (
    <Icon {...props}>
      <title>{media}</title>
      {media === 'linkedin' && <Linkedin />}
      {media === 'pinterest' && <Pinterest />}
      {media === 'snapchat' && <Snapchat />}
      {media === 'tiktok' && <Tiktok />}
      {media === 'tumblr' && <Tumblr />}
      {media === 'twitter' && <Twitter />}
      {media === 'vimeo' && <Vimeo />}
      {media === 'youtube' && <Youtube />}
      {media === 'facebook' && <Facebook />}
      {media === 'instagram' && <Instagram />}
    </Icon>
  );
}

